using UnityEngine;using UnityEngine.UI;using System.Collections;public class MathController : MonoBehaviour {    public Text equationText;    float lastUpdate = 0;    EquationGenerator generator;        	// Use this for initialization	void Start () {                Random r = new Random();        generator = new EquationGenerator();
    }		// Update is called once per frame	void Update () {        if (Time.time - lastUpdate > 2)        {            lastUpdate = Time.time;            setEquation();        }       	}    private void setEquation()    {        Equation equation = generator.generate(Equation.Operator.MULTIPLY);        equationText.text = equation.leftOperand + "x" + equation.rightOperand + "=" + equation.getResult();        }}