using UnityEngine;using System.Collections;public class EquationGenerator {	public Equation generate(Equation.Operator op) {        Equation generated = null;        switch (op) {            case Equation.Operator.ADD:                generated = generateAdd();                break;            case Equation.Operator.SUBTRACT:                generated = generateSubtract();                break;            case Equation.Operator.MULTIPLY:                generated = generateMultiply();                break;                             }        return generated;    }    private Equation generateAdd() {        return new AdditionEquation(Random.Range(0,100), Random.Range(0, 100));    }    private Equation generateSubtract() {
        return new SubtractionEquation(Random.Range(0, 100), Random.Range(0, 100));    }    private Equation generateMultiply() {
        return new MultiplicationEquation(Random.Range(0, 100), Random.Range(0, 100));    }    }